#define ENET_BWR_RACC_SHIFT16(base, value) (BME_BFI32(&ENET_RACC_REG(base), ((uint32_t)(value) << ENET_RACC_SHIFT16_SHIFT), ENET_RACC_SHIFT16_SHIFT, ENET_RACC_SHIFT16_WIDTH))
