#define HSADC_RMW_SDIS(base, mask, value) (HSADC_WR_SDIS(base, (HSADC_RD_SDIS(base) & ~(mask)) | (value)))
#define HSADC_SET_SDIS(base, value) (BME_OR16(&HSADC_SDIS_REG(base), (uint16_t)(value)))
