static void PrintInASCII(uint8_t *buf, size_t bufSize, size_t LineLen, uint8_t nonASCIIchar, uint8_t fillChar, const CLS1_StdIOType *io) {
  unsigned int i;
  uint8_t ch;

  /* print in ASCII */
  CLS1_SendCh(' ', io->stdOut);
  for (i=0; i<bufSize; i++) {
    ch = buf[i];
    if (ch >= ' ' && ch <= 0x7f) {
      CLS1_SendCh(ch, io->stdOut);
    } else {
      CLS1_SendCh(nonASCIIchar, io->stdOut); /* place holder */
    }
  }
  for (/*empty*/; i<LineLen; i++) { /* fill up line */
    CLS1_SendCh(fillChar, io->stdOut);
  }
}

/*!
 * \brief Writes a directory listing of the given path
 * \param[in] dirPathPtr Pointer to a directory path string. Pass "\\" for the root directory
 * \param[in] io Callback to write directory output
 * \return Error code, otherwise ERR_OK
 */
static uint8_t PrintDir(const uint8_t *dirPathPtr, const CLS1_StdIOType *io) {
  FAT1_FILINFO fInfo;
  FAT1_FRESULT fres;
  uint32_t p1;
  UINT s1, s2;
  FAT1_DIR dir;  /* Directory object */
  uint8_t buf[sizeof("yyyy-mm-dd hh:ss")+1];
#if !FAT1_FS_READONLY
  FAT1_FATFS *fs;
#endif

  CLS1_SendStr((unsigned char*)"Directory of ", io->stdOut);
  CLS1_SendStr(dirPathPtr, io->stdOut);
  CLS1_SendStr((unsigned char*)"\r\n", io->stdOut);
  fres = FAT1_f_opendir(&dir, (const TCHAR*)dirPathPtr);
  if (fres != FR_OK) {
    FatFsFResultMsg((unsigned char*)"opendir failed", fres, io);
    return ERR_FAULT;
  }
  p1 = s1 = s2 = 0;
  for(;;) {
    fres = FAT1_readdir(&dir, &fInfo);
    if (fres != FR_OK) {
      FatFsFResultMsg((unsigned char*)"readdir failed", fres, io);
      break;
    }
    if (!fInfo.fname[0]) { /* end of directory list */
      break;
    }
    /* file attributes */
    if (fInfo.fattrib & AM_DIR) { /* directory */
      s2++;
      io->stdOut('D');
    } else {
      s1++;
      p1 += fInfo.fsize;
      io->stdOut('-');
    }
    if (fInfo.fattrib & AM_RDO) { /* read only */
      io->stdOut('R');
    } else {
      io->stdOut('-');
    }
    if (fInfo.fattrib & AM_HID) { /* hidden */
      io->stdOut('H');
    } else {
      io->stdOut('-');
    }
    if (fInfo.fattrib & AM_SYS) { /* system */
      io->stdOut('S');
    } else {
      io->stdOut('-');
    }
    if (fInfo.fattrib & AM_ARC) { /* archive */
      io->stdOut('A');
    } else {
      io->stdOut('-');
    }
    io->stdOut(' ');
    /* file date & time */
    buf[0] = '\0';
    UTIL1_strcatNum16sFormatted(buf, sizeof(buf), (int16_t)((fInfo.fdate >> 9) + 1980), ' ', 4); /* year */
    UTIL1_strcat(buf, sizeof(buf), (unsigned char*)"-");
    UTIL1_strcatNum16sFormatted(buf, sizeof(buf), (int16_t)((fInfo.fdate >> 5) & 15), '0', 2); /* month */
    UTIL1_strcat(buf, sizeof(buf), (unsigned char*)"-");
    UTIL1_strcatNum16sFormatted(buf, sizeof(buf), (int16_t)(fInfo.fdate & 31), '0', 2); /* day */
    UTIL1_strcat(buf, sizeof(buf), (unsigned char*)" ");
    UTIL1_strcatNum16sFormatted(buf, sizeof(buf), (int16_t)((fInfo.ftime >> 11)), '0', 2); /* hour */
    UTIL1_strcat(buf, sizeof(buf), (unsigned char*)":");
    UTIL1_strcatNum16sFormatted(buf, sizeof(buf), (int16_t)((fInfo.ftime >> 5) & 63), '0', 2); /* minute */
    CLS1_SendStr(buf, io->stdOut);
    io->stdOut(' ');
    buf[0] = '\0';
    /* file size */
    UTIL1_strcatNum32uFormatted(buf, sizeof(buf), fInfo.fsize, ' ', 10); /* size */
    CLS1_SendStr(buf, io->stdOut);
    /* file name */
    io->stdOut(' ');
    CLS1_SendStr((unsigned char*)fInfo.fname, io->stdOut);
#if FAT1_USE_LFN
    io->stdOut(' ');
    CLS1_SendStr((unsigned char*)fInfo.altname, io->stdOut);
#endif
    io->stdOut('\r');
    io->stdOut('\n');
  }
  /* number of files and bytes used */
  buf[0] = '\0';
  UTIL1_strcatNum16u(buf, sizeof(buf), (uint16_t)s1);
  CLS1_SendStr(buf, io->stdOut);
  CLS1_SendStr((unsigned char*)" File(s), ", io->stdOut);
  buf[0] = '\0';
  UTIL1_strcatNum32u(buf, sizeof(buf), p1);
  CLS1_SendStr(buf, io->stdOut);
  CLS1_SendStr((unsigned char*)" bytes total\r\n", io->stdOut);
  /* number of directories and number of free bytes */
  buf[0] = '\0';
  UTIL1_strcatNum16u(buf, sizeof(buf), (uint16_t)s2);
  CLS1_SendStr(buf, io->stdOut);
  CLS1_SendStr((unsigned char*)" Dir(s)", io->stdOut);
#if !FAT1_FS_READONLY
  /* number of free bytes */
  fres = FAT1_getfree((const TCHAR*)dirPathPtr, &p1, &fs);
  if (fres != FR_OK) {
    FatFsFResultMsg((unsigned char*)"getfree failed", fres, io);
  } else {
    io->stdOut(',');
    io->stdOut(' ');
    buf[0] = '\0';
    UTIL1_strcatNum32s(buf, sizeof(buf), (long)(p1*fs->csize/2));
    CLS1_SendStr(buf, io->stdOut);
    CLS1_SendStr((unsigned char*)" KBytes free", io->stdOut);
  }
#endif
  (void)FAT1_f_closedir(&dir); /* close directory */
  io->stdOut('\r');
  io->stdOut('\n');
  return ERR_OK;
}

static uint8_t DirCmd(const unsigned char *cmd, const CLS1_ConstStdIOType *io) {
  /* precondition: cmd starts with "dir" */
  uint8_t res = ERR_OK;
  FAT1_DEF_NAMEBUF(fileName);

  FAT1_INIT_NAMEBUF(fileName);
  if (*(cmd+sizeof("dir")-1)== ' ') { /* space after "dir": read name */
    if (UTIL1_ReadEscapedName(cmd+sizeof("dir"), FAT1_PTR_NAMEBUF(fileName),
          FAT1_SIZE_NAMEBUF(fileName), NULL, NULL, NULL)==ERR_OK
       )
    {
      /* ok, have now directory name */
    } else {
      CLS1_SendStr((unsigned char*)"reading directory name failed!\r\n", io->stdErr);
      res = ERR_FAILED;
    }
  } else { /* use current directory */
#if FAT1_FS_RPATH >= 2
    FAT1_FRESULT fres;
    fres = FAT1_getcwd((TCHAR*)FAT1_PTR_NAMEBUF(fileName), FAT1_SIZE_NAMEBUF(fileName));
    if(fres!=FR_OK) {
      FatFsFResultMsg((unsigned char*)"getcwd failed", fres, io);
      res = ERR_FAILED;
    } else {
      /* ok, have now directory name */
    }
#else
    UTIL1_strcpy(FAT1_PTR_NAMEBUF(fileName), FAT1_SIZE_NAMEBUF(fileName), (unsigned char*)"\\"); /* use root */
#endif
  }
  if (res == ERR_OK) {
    res = FAT1_PrintDirectory(FAT1_PTR_NAMEBUF(fileName), io);
  }
  FAT1_FREE_NAMEBUF(fileName);
  return res;
}

