static uint8_t CdCmd(const unsigned char *cmd, const CLS1_ConstStdIOType *io) {
  /* precondition: cmd starts with "cd" */
#if FAT1_FS_RPATH > 0
  uint8_t res = ERR_OK;
  FAT1_DEF_NAMEBUF(fileName);

  FAT1_INIT_NAMEBUF(fileName);
  if (*(cmd+sizeof("cd")-1)== ' ') { /* space after "cd": read name */
    if (UTIL1_ReadEscapedName(cmd+sizeof("cd"), FAT1_PTR_NAMEBUF(fileName),
          FAT1_SIZE_NAMEBUF(fileName), NULL, NULL, NULL)==ERR_OK
       )
    {
      res = FAT1_ChangeDirectory(FAT1_PTR_NAMEBUF(fileName), io);
    } else {
      CLS1_SendStr((unsigned char*)"reading directory name failed!\r\n", io->stdErr);
      res = ERR_FAILED;
    }
  } else { /* print current directory */
#if FAT1_FS_RPATH >= 2
    FAT1_FRESULT fres;

    fres = FAT1_getcwd((TCHAR*)FAT1_PTR_NAMEBUF(fileName), FAT1_SIZE_NAMEBUF(fileName));
    if(fres!=FR_OK) {
      FatFsFResultMsg((unsigned char*)"getcwd failed", fres, io);
      res = ERR_FAILED;
    } else {
      CLS1_SendStr(FAT1_PTR_NAMEBUF(fileName), io->stdOut);
      CLS1_SendStr((unsigned char*)"\r\n", io->stdOut);
    }
#else
    UTIL1_strcpy(FAT1_PTR_NAMEBUF(fileName), FAT1_SIZE_NAMEBUF(fileName), (unsigned char*)"\\"); /* use root */
#endif
  }
  FAT1_FREE_NAMEBUF(fileName);
  return res;
#else
  (void)cmd;
  #warning "relative directories not enabled in FatFS!"
  CLS1_SendStr((unsigned char*)"relative directories not available in file system\r\n", io->stdErr);
  return ERR_FAILED;
#endif
}


