#if !_FS_READONLY
/*
** ===================================================================
**     Method      :  FAT1_get_fattime (component FAT_FileSystem)
**     Description :
**         Returns the current time
**     Parameters  : None
**     Returns     :
**         ---             - Error code
** ===================================================================
*/
uint32_t FAT1_get_fattime(void)
{
  /* 31-25: Year(0-127 org.1980), 24-21: Month(1-12), 20-16: Day(1-31) */
  /* 15-11: Hour(0-23), 10-5: Minute(0-59), 4-0: Second(0-29 *2) */
  /* Pack date and time into a uint32_t variable */
  TIMEREC time;
  DATEREC date;
  uint8_t res;

  res = TmDt1_GetTime(&time);           /* get time information */
  if (res!=ERR_OK) {
    return 0; /* failed */
  }
  res = TmDt1_GetDate(&date);           /* get date information */
  if (res!=ERR_OK) {
    return 0; /* failed */
  }
  return ((uint32_t)(date.Year - 1980) << 25)
       | ((uint32_t)date.Month << 21)
       | ((uint32_t)date.Day << 16)
       | ((uint32_t)time.Hour << 11)
       | ((uint32_t)time.Min << 5)
       | ((uint32_t)time.Sec);
}

uint32_t get_fattime(void) {
  return FAT1_get_fattime();
}
#endif /*!_FS_READONLY*/


/*
** ===================================================================
**     Method      :  FAT1_ParseCommand (component FAT_FileSystem)
**     Description :
**         Shell Command Line parser. This method is enabled/disabled
**         depending on if you have the Shell enabled/disabled in the
**         properties.
**     Parameters  :
**         NAME            - DESCRIPTION
**       * cmd             - Pointer to command string
**       * handled         - Pointer to variable which tells if
**                           the command has been handled or not
**       * io              - Pointer to I/O structure
**     Returns     :
**         ---             - Error code
** ===================================================================
*/
uint8_t FAT1_ParseCommand(const unsigned char *cmd, bool *handled, const CLS1_StdIOType *io)
{
  if (UTIL1_strcmp((char*)cmd, CLS1_CMD_HELP)==0 || UTIL1_strcmp((char*)cmd, "FAT1 help")==0) {
    *handled = TRUE;
    return PrintHelp(io);
  } else if ((UTIL1_strcmp((char*)cmd, CLS1_CMD_STATUS)==0) || (UTIL1_strcmp((char*)cmd, "FAT1 status")==0)) {
    *handled = TRUE;
    return PrintStatus(io);
  } else if (UTIL1_strncmp((char*)cmd, "FAT1 cd", sizeof("FAT1 cd")-1)==0) {
    *handled = TRUE;
    return CdCmd(cmd+sizeof("FAT1"), io);
  } else if (UTIL1_strncmp((char*)cmd, "FAT1 dir", sizeof("FAT1 dir")-1)==0) {
    *handled = TRUE;
    return DirCmd(cmd+sizeof("FAT1"), io);
  } else if (UTIL1_strncmp((char*)cmd, "FAT1 copy ", sizeof("FAT1 copy ")-1)==0) {
    *handled = TRUE;
    return CopyCmd(cmd+sizeof("FAT1"), io);
  } else if (UTIL1_strncmp((char*)cmd, "FAT1 delete ", sizeof("FAT1 delete ")-1)==0) {
    *handled = TRUE;
    return DeleteCmd(cmd+sizeof("FAT1"), io);
  } else if (UTIL1_strncmp((char*)cmd, "FAT1 create ", sizeof("FAT1 create ")-1)==0) {
    *handled = TRUE;
    return CreateCmd(cmd+sizeof("FAT1"), io);
  } else if (UTIL1_strncmp((char*)cmd, "FAT1 mkdir ", sizeof("FAT1 mkdir ")-1)==0) {
    *handled = TRUE;
    return MkdirCmd(cmd+sizeof("FAT1"), io);
  } else if (UTIL1_strncmp((char*)cmd, "FAT1 rename ", sizeof("FAT1 rename ")-1)==0) {
    *handled = TRUE;
    return RenameCmd(cmd+sizeof("FAT1"), io);
  } else if (UTIL1_strncmp((char*)cmd, "FAT1 printhex ", sizeof("FAT1 printhex ")-1)==0) {
    *handled = TRUE;
    return PrintHexCmd(cmd+sizeof("FAT1"), io);
  } else if (UTIL1_strncmp((char*)cmd, "FAT1 printsector ", sizeof("FAT1 printsector ")-1)==0) {
    *handled = TRUE;
    return SectorCmd(cmd+sizeof("FAT1"), io);
  } else if (UTIL1_strncmp((char*)cmd, "FAT1 print ", sizeof("FAT1 print ")-1)==0) {
    *handled = TRUE;
    return PrintCmd(cmd+sizeof("FAT1"), io);
  } else if (UTIL1_strcmp((char*)cmd, "FAT1 diskinfo")==0) {
    *handled = TRUE;
    return FAT1_PrintDiskInfo(NULL, io);
  } else if (UTIL1_strcmp((char*)cmd, "FAT1 benchmark")==0) {
    *handled = TRUE;
    return FAT1_Benchmark(io);
  }
  return ERR_OK;
}

/*
** ===================================================================
**     Method      :  FAT1_CheckCardPresence (component FAT_FileSystem)
**     Description :
**         This method checks if card has been inserted or removed and
**         mounts or unmounts the file system.
**     Parameters  :
**         NAME            - DESCRIPTION
**       * cardMounted     - Pointer to initialize this
**                           variable to FALSE on the caller side the
**                           first time.
**       * drive           - drive string, or ""
**       * fileSystemObject - Pointer to file
**                           system object
**         io              - Pointer to io handler to be used. Can be
**                           NULL, then no messages are written.
**     Returns     :
**         ---             - Error code
** ===================================================================
*/
uint8_t FAT1_CheckCardPresence(bool *cardMounted, uint8_t *drive, FATFS *fileSystemObject, const CLS1_StdIOType *io)
{
  if (drive==NULL) { /* backward compatibility with drive numbers before (which could be zero or NULL) */
    drive = (unsigned char*)"";
  }
  if (!(*cardMounted) && FAT1_isDiskPresent(drive)) {
    /* card inserted */
    if (FAT1_MountFileSystem(fileSystemObject, drive, io)==ERR_OK) {
      *cardMounted = TRUE;
      if (io!=NULL) {
        CLS1_SendStr((unsigned char*)"File System mounted\r\n", io->stdOut);
      }
    } else {
      return ERR_FAILED;
    }
  } else if (*cardMounted && !FAT1_isDiskPresent(drive)) {
    /* card removed */
    if (FAT1_UnMountFileSystem(drive, io)==ERR_OK) {
      *cardMounted = FALSE;
      if (io!=NULL) {
        CLS1_SendStr((unsigned char*)"File System unmounted\r\n", io->stdOut);
      }
    } else {
      return ERR_FAILED;
    }
  }
  return ERR_OK;
}

/*
** ===================================================================
**     Method      :  FAT1_MountFileSystem (component FAT_FileSystem)
**     Description :
**         Mounts a file system
**     Parameters  :
**         NAME            - DESCRIPTION
**       * fileSystemObject - Pointer to a
**                           file system object
**       * logicalDrive    - Pointer to the drive
**                           string, or ""
**       * io              - Pointer to I/O handler
**     Returns     :
**         ---             - Error code
** ===================================================================
*/
/*!
 * \brief Mounts the file system for a drive
 * \param[in] fileSystemObject Pointer to the file system object
 * \param[in] logicalDrive The drive number to be used
 * \param[in] io IO handler for output
 * \return Error code, ERR_OK for success.
 */
uint8_t FAT1_MountFileSystem(FATFS *fileSystemObject, uint8_t *logicalDrive, const CLS1_StdIOType *io)
{
  FAT1_FRESULT fres;

  fres = FAT1_mount(fileSystemObject, (const TCHAR*)logicalDrive, 0);
  if (fres != FR_OK) {
    if (io!=NULL) {
      FatFsFResultMsg((unsigned char*)"mount failed", fres, io);
    }
    return ERR_FAILED;
  }
  return ERR_OK;
}

