/*
** ===================================================================
**     Method      :  FAT1_CopyFile (component FAT_FileSystem)
**     Description :
**         Copy a file
**     Parameters  :
**         NAME            - DESCRIPTION
**         srcFileName     - Source file name
**         dstFileName     - Destination file name
**         io              - Pointer to I/O handler
**     Returns     :
**         ---             - Error code
** ===================================================================
*/
/*!
 * \brief Copy the source file to a destination file
 * \param[in] srcFileName Source file name
 * \param[in] dstFileName Destination file name
 * \param[in] io IO handler for output
 * \return Error code, ERR_OK for success.
 */
uint8_t FAT1_CopyFile(const uint8_t*srcFileName, const uint8_t *dstFileName, const CLS1_StdIOType *io)
{
#if !FAT1_FS_READONLY
  FAT1_FIL fsrc, fdst;  /* file objects */
  FAT1_FRESULT fres;
  uint8_t buffer[32];   /* copy buffer */
  UINT br, bw;          /* file read/write counters */
  uint8_t res =  ERR_OK;

  if (FAT1_isWriteProtected((uint8_t*)"")) {
    CLS1_SendStr((unsigned char*)"disk is write protected!\r\n", io->stdErr);
    return ERR_FAILED;
  }
  /* open source file */
  fres = FAT1_open(&fsrc, (const TCHAR*)srcFileName, FA_OPEN_EXISTING | FA_READ);
  if (fres != FR_OK) {
    FatFsFResultMsg((unsigned char*)"open source file failed", fres, io);
    return ERR_FAILED;
  }
  /* create destination file */
  fres = FAT1_open(&fdst, (const TCHAR*)dstFileName, FA_CREATE_ALWAYS | FA_WRITE);
  if (fres != FR_OK) {
    (void)FAT1_close(&fsrc); /* close the source file which we have open */
    FatFsFResultMsg((unsigned char*)"open destination file failed", fres, io);
    return ERR_FAILED;
  }
  /* now copy source to destination */
  for (;;) {
    fres = FAT1_read(&fsrc, buffer, sizeof(buffer), &br);
    if (fres != FR_OK) {
      FatFsFResultMsg((unsigned char*)"reading source file failed", fres, io);
      res = ERR_FAILED;
      break;
    }
    if (br == 0) { /* EOF */
      break; /* get out of loop */
    }
    fres = FAT1_write(&fdst, buffer, br, &bw);
    if (fres != FR_OK) {
      FatFsFResultMsg((unsigned char*)"writing destination file failed", fres, io);
      res = ERR_FAILED;
      break;
    }
    if (bw < br) {
      CLS1_SendStr((unsigned char*)"failed writing destination file, or disk full\r\n", io->stdErr);
      res = ERR_FAILED;
      break;
    }
  } /* for */
  /* close all files */
  fres = FAT1_close(&fsrc);
  if (fres != FR_OK) {
    FatFsFResultMsg((unsigned char*)"closing source file failed", fres, io);
    res = ERR_FAILED;
  }
  fres = FAT1_close(&fdst);
  if (fres != FR_OK) {
    FatFsFResultMsg((unsigned char*)"closing destination file failed", fres, io);
    res = ERR_FAILED;
  }
  return res;
#else
  (void)srcFileName; /* unused argument */
  (void)dstFileName; /* unused argument */
  CLS1_SendStr((unsigned char*)"File System is in Read-Only mode\r\n", io->stdErr);
  return ERR_FAILED;
#endif
}

/*
** ===================================================================
**     Method      :  FAT1_DeleteFile (component FAT_FileSystem)
**     Description :
**         Deletes a file
**     Parameters  :
**         NAME            - DESCRIPTION
**         fileName        - Filename of file to be deleted
**         io              - Pointer to I/O handler
**     Returns     :
**         ---             - Error code
** ===================================================================
*/
/*!
 * \brief Deletes a file
 * \param[in] fileName Name of file to be deleted
 * \param[in] io IO handler for output
 * \return Error code, ERR_OK for success.
 */
uint8_t FAT1_DeleteFile(const uint8_t *fileName, const CLS1_StdIOType *io)
{
  FAT1_FRESULT fres;

  if (FAT1_isWriteProtected((uint8_t*)"")) {
    CLS1_SendStr((unsigned char*)"disk is write protected!\r\n", io->stdErr);
    return ERR_FAILED;
  }
  fres = FAT1_unlink((const TCHAR*)fileName);
  if (fres != FR_OK) {
    FatFsFResultMsg((unsigned char*)"unlink failed", fres, io);
    return ERR_FAILED;
  }
  return ERR_OK;
}

